import React, { Component } from 'react';

import './list.scss';
import ToDoItem from './ToDoItem';


export default class ToDoList extends Component{
    constructor(props){
        super(props);
    
        this.state = {
            show: true
        };
    }

    onDeleted = () => {
        this.setState({
            show: false
        })
    }


    render() {
        // console.log(this.state.show);
        let remove = this.onDeleted;
        return (       
            this.props.items.map(function(index, item){
            return (
                <li 
                    className={(index.important ? 'list-group-item important' : 'list-group-item')
                                + (this.state.show ? '' : 'hidden')}
                    key={item}>
                    <ToDoItem 
                            label={index.label} 
                            important={index.important}
                            onDeleted={remove}
                    />              
                </li>   
                )
            })              
        )
    }
}
