import React, { Component } from 'react';

import './list.scss';
import ToDoItem from './ToDoItem';


export default class ToDoList extends Component{
    constructor(props){
        super(props);
    
        this.state = {
            // show: true
        };
    }

    // index.important ? 'list-group-item important' : 'list-group-item'
    // show ? 'list-group-item' : 'hidden'

    render() {
        // console.log(this.state.show);
        // let remove = this.onDeleted;
        let show = this.state.show;
        return (       
            this.props.items.map(function(index, item){
            return (
                <li 
                    className={show ? 'list-group-item' : 'hidden'}
                    key={item}>
                    <ToDoItem 
                            label={index.label} 
                            important={index.important}
                            // onDeleted={remove}
                    />              
                </li>   
                )
            })              
        )
    }
}
