import React, { Component } from 'react';

import './list.scss';
import ToDoItem from './ToDoItem';
import AppHeader from '../header/AppHeader';
import SearchPanel from '../search/SearchPanel';

const items = [
    {
        label: "Drink Coffee",
        important: false
    },
    {
        label: "Make Awesome App",
        important: true
    },
    {
        label: "Have a lunch",
        important: false
    }
]

export default class ToDoList extends Component{
    constructor(props){
        super(props);
    
        this.state = {
            items: items,
            // important: this.items.map = (index) => index.important
        };
    }
   
    onRemove = () => {
        this.setState({
            show: false
        })
    }

    onAddItem = () => {    
        let liItem = this.state.items.map(function(index, item){
            return (
                <li className={this.state.important ? 'list-group-item important' : 'list-group-item'} key={item}>
                    <ToDoItem label={index.label}/>    
                    <div class="btns">
                    <button 
                        type="button" 
                        class="btn btn-outline-danger"
                        onClick={this.onRemove}>>
                        <i class="far fa-trash-alt"></i>
                    </button>
                    <button 
                        type="button" 
                        class="btn btn-outline-success"
                        onClick={this.onDone}>
                        <i class="fas fa-exclamation"></i>
                    </button>
                </div>           
                </li>   
            )
        })
        return liItem;
    }

    render() {
        return (
            <div className="container">
                <AppHeader length = {this.state.items.length}/>
                <SearchPanel/>
                <ul className="list-group">
                    {this.onAddItem()}
                </ul>           
            </div>     
        )
    }
}
