import React, { Component } from 'react';

export default class ToDoItem extends Component {

    constructor(props){
        super(props);
    
        this.state = {
            done: false,
            important: false,
            show: true
        };
    }

    onDone = () => {
        this.setState({
            done: !this.state.done
        })
    }


    render() {
        return (  
                <span onClick={this.onDone}> className={this.state.check ? 'item checked' : 'item'}>
                    {this.props.label}
                </span>            
        )
    }
}
