import React, { Component } from 'react';

import AppHeader from './header/AppHeader';
import SearchPanel from './search/SearchPanel';
import ToDoList from './list/ToDoList';
import AddItem from './addItem/AddItem';

let items = [
    {
        label: "Drink Coffee",
        important: false
    },
    {
        label: "Make Awesome App",
        important: true
    },
    {
        label: "Have a lunch",
        important: false
    }
]

export default class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            items: items
        }
    }

    // updateItem = (id, itemAttributes) => {
    //     var index = this.state.items.findIndex(x=> x.id === id);
    //     if (index === -1){

    //     }

    //     else
    //       this.setState({
    //         items: [
    //            ...this.state.items.slice(0,index),
    //            Object.assign({}, this.state.items[index], itemAttributes),
    //            ...this.state.items.slice(index+1)
    //         ]
    //       });
    // }

    updateItem = (index) => {

        const list = this.state.items; //клон items array
        const item = list[index]; // items[item] кликнутый итем
        item.important = true; //меняем текушее значение на 
        list[index] = item; //обновляем клон
        this.setState({
            items: list  //обновляем state
        });
    }

    removeItem = (value) => {
        items.splice(value, 1);
        this.setState({
            items: items
        })
    }

    render() {
        return (
            <div className="container">
                <AppHeader length = {this.state.items.length}/>
                <SearchPanel />
                <ToDoList 
                    items = {this.state.items}
                    removeItem = {this.removeItem}
                    updateItem={this.updateItem}
                />   
                <AddItem />       
            </div>     
        )
    }
}
