import React, { Component } from 'react';

import './list.scss';
import ToDoItem from './ToDoItem';
import AppHeader from '../header/AppHeader';
import SearchPanel from '../search/SearchPanel';

const items = [
    {
        label: "Drink Coffee",
        important: false
    },
    {
        label: "Make Awesome App",
        important: true
    },
    {
        label: "Have a lunch",
        important: false
    }
]

export default class ToDoList extends Component{
    constructor(props){
        super(props);
    
        this.state = {
            show: true,
            items: items
        };
    }

    

    onAddItem = () => {    
        let liItem = this.state.items.map(function(index, item){
            return (
                <li className={index.important ? 'list-group-item important' : 'list-group-item'} key={item}>
                    <ToDoItem 
                            label={index.label} 
                            important={index.important}
                            onDeleted={this.set}
                    />              
                </li>   
            )
        })
        return liItem;
    }

    render() {
        return (
            <div className="container">
                <AppHeader length = {this.state.items.length}/>
                <SearchPanel/>
                <ul className="list-group">
                    {this.onAddItem()}
                </ul>           
            </div>     
        )
    }
}
