import React, { Component } from 'react';

import ToDoItem from './ToDoItem';
import AppHeader from './AppHeader';
import SearchPanel from './SearchPanel';

const items = [
    {
        label: "Drink Coffee",
        important: false
    },
    {
        label: "Build React App",
        important: true
    }
]

export default class ToDoList extends Component{
    constructor(props){
        super(props);
    
        this.state = {
            items: items
        };
    }

    onAddItem() {
        this.state.items.map(function(index, item){
            let liColor = {
                color: index.important ? 'tomato': 'black'
            }
            return <li className="list-group-item" key={item} style={liColor}><ToDoItem label={index.label} /></li>   
        })
    }

    render() {
        return (
            <div class="container">
                <AppHeader/>
                <SearchPanel/>
                <ul className="list-group">
                    {this.onAddItem}
                </ul>           
            </div>     
        )
    }
}
