import React, { Component } from 'react';

export default class ToDoItem extends Component {

    constructor(props){
        super(props);
    
        this.state = {
            done: false,
            important: this.props.important,
            show: true
        };
    }

    onDone = () => {
        this.setState({
            done: !this.state.done
        })
    }

    onImportant = () => {
        console.log(this.state.important)
        this.setState({
            important: !this.state.important          
        })
        console.log(this.state.important)
    };

    onDeleted = () => {
        this.setState({
            show: false
        })
    }

        // this.props.important ? 'list-group-item important' : 'list-group-item'

    render() {
        return (  
            <li 
                className={
                            [this.props.important ? 'list-group-item important' : 'list-group-item', this.state.show ? '' : 'hidden'] 
                            .join(' ')}         
                >
            <span 
                className='flex'
                >
                <span               
                    className={this.state.done ? 'item checked' : 'item'}
                    onClick={this.onDone}>
                    {this.props.label}
                </span>   
                
                <div className="btns">
                    <button 
                        type="button" 
                        className="btn btn-outline-danger"
                        onClick={this.onDeleted}>                       
                        <i className="far fa-trash-alt"></i>
                    </button>
                    <button 
                        type="button" 
                        className="btn btn-outline-success"
                        onClick={this.onImportant}>
                        <i className="fas fa-exclamation"></i>
                    </button>
                </div>
            </span>
            </li>
        )
    }
}
