import React, { Component } from 'react';

export default class ToDoItem extends Component {

    constructor(props){
        super(props);
    
        this.state = {
            done: false,
            show: true
        };
    }

    onDone = () => {
        this.setState({
            done: !this.state.done
        })
    }

    onRemove = () => {
        this.setState({
            show: false
        })
    }

    render() {
        return (  
            <span 
                className="flex" 
                onClick={this.onDone}>
                <span className={this.state.check ? 'item checked' : 'item'}>
                    {this.props.label}
                </span>
                <div class="btns">
                    <button 
                        type="button" 
                        class="btn btn-outline-danger"
                        onClick={this.onRemove}>>
                        <i class="far fa-trash-alt"></i>
                    </button>
                    <button 
                        type="button" 
                        class="btn btn-outline-success"
                        onClick={this.onDone}>
                        <i class="fas fa-exclamation"></i>
                    </button>
                </div>    
            </span>              
        )
    }
}
