import React, { Component } from 'react';

// import items from '../data/item';

const items = [
    {
        label: "Drink Coffee",
        important: false
    },
    {
        label: "Build React App",
        important: true
    }
]

export default class ToDoItem extends Component {
    constructor(props){
        super(props);
    
        this.state = {
            items: items
        };
    }

      


  render() {
        const liColor = {
            color: this.state.important ? 'tomato': 'black'
        };
        return (
            this.state.items.map( (index) => 
                // var color = index.important ? 'tomato' : 'black'},
                <li style={liColor}>{index.label}</li>           
            )
        )
  }
}
